import React, { useEffect } from 'react';
import {jwtDecode} from 'jwt-decode'; // Importation par défaut
import { getToken } from '../api';
import './EventList.css';
import { format, parseISO, isValid } from 'date-fns';

const Event = ({ event, onDelete, onAttend, onUnattend }) => {
  // Get user ID from token
  const token = getToken();
  const decodedToken = jwtDecode(token);
  const userId = decodedToken.id;

  const isAttendee = event.attendees.some(attendee => attendee._id === userId);

  useEffect(() => {
    console.log('Event updated:', event);
  }, [event]);

  const handleToggleAttend = () => {
    if (isAttendee) {
      onUnattend(event._id);
    } else {
      onAttend(event._id);
    }
  };
  const parsedDate = parseISO(event.date);
  const formattedDate = isValid(parsedDate) ? format(parsedDate, 'dd/MM/yyyy HH:mm:ss') : 'Invalid date';
  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleCancelEdit = () => {
    setIsEditing(false);
    // Réinitialise les détails de l'événement aux valeurs actuelles
    setEventDetails({
      title: event.title,
      description: event.description,
      date: event.date,
      maxAttendees: event.maxAttendees
    });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEventDetails({
      ...eventDetails,
      [name]: value
    });
  };

  const handleUpdateEvent = (e) => {
    e.preventDefault();
    // Appeler la fonction de mise à jour d'événement passée en tant que prop
    updateEvent(event._id, eventDetails);
    setIsEditing(false);
  };

  return (
    <div className="event">
      {isEditing ? (
        <form onSubmit={handleUpdateEvent}>
          <input
            type="text"
            name="title"
            value={eventDetails.title}
            onChange={handleInputChange}
            placeholder="Event Title"
          />
          <textarea
            name="description"
            value={eventDetails.description}
            onChange={handleInputChange}
            placeholder="Event Description"
          ></textarea>
          <input
            type="date"
            name="date"
            value={eventDetails.date}
            onChange={handleInputChange}
          />
          <input
            type="number"
            name="maxAttendees"
            value={eventDetails.maxAttendees}
            onChange={handleInputChange}
            placeholder="Max Attendees"
          />
          <button type="submit">Save</button>
          <button type="button" onClick={handleCancelEdit}>Cancel</button>
        </form>
      ) : (
        <>
          <h3>{event.title}</h3>
          <p>{event.description}</p>
          <p>{formattedDate}</p>
          <p>Created by: {event.createdBy.name}</p>
          <p>Attendees: {event.attendees.length} / {event.maxAttendees}</p>

          {event.createdBy._id !== userId && (
            <button
              className={isAttendee ? 'unattend' : 'attend'}
              onClick={handleToggleAttend}
            >
              {isAttendee ? 'Unattend' : 'Attend'}
            </button>
          )}

          {event.createdBy._id === userId && (
            <button className="update" onClick={() => setIsEditing(true)}>
              Update
            </button>
          )}

          {event.createdBy._id === userId && (
            <button className="delete" onClick={() => onDelete(event._id)}>
              Delete
            </button>
          )}
        </>
      )}
    </div>
  );
};

export default Event;
