import React, { useState, useEffect } from 'react';
import { fetchEvents, deleteEvent, attendEvent, unattendEvent } from '../api';
import EventForm from './EventForm';
import Event from './Event';

const EventList = () => {
  const [events, setEvents] = useState([]);
  const [eventToUpdate, setEventToUpdate] = useState(null); // State to track event being updated

  useEffect(() => {
    // Fetch events on component mount
    fetchEvents().then(data => {
      setEvents(data);
    });
  }, []);

  const handleEventCreated = async (newEvent) => {
    try {
      // Logic to add new event to state
    } catch (error) {
      console.error('Error creating event:', error);
    }
  };

  const handleUpdateEvent = (eventId) => {
    const eventToUpdate = events.find(event => event._id === eventId);
    setEventToUpdate(eventToUpdate);
  };

  const handleDelete = async (eventId) => {
    try {
      // Logic to delete event
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  };

  const handleAttend = async (eventId) => {
    try {
      // Logic to attend event
    } catch (error) {
      console.error('Error attending event:', error);
    }
  };

  const handleUnattend = async (eventId) => {
    try {
      // Logic to unattend event
    } catch (error) {
      console.error('Error unattending event:', error);
    }
  };

  return (
    <div>
      {/* EventForm for creating or updating events */}
      <EventForm onEventCreated={handleEventCreated} eventToUpdate={eventToUpdate} />

      {/* Render list of events */}
      {events.map(event => (
        <Event
          key={event._id}
          event={event}
          onUpdate={handleUpdateEvent}
          onDelete={handleDelete}
          onAttend={handleAttend}
          onUnattend={handleUnattend}
        />
      ))}
    </div>
  );
};

export default EventList;
