import React, { useEffect } from 'react';
import {jwtDecode} from 'jwt-decode';
import { getToken } from '../api';
import './EventList.css'; // Importer le fichier CSS

const Event = ({ event, onDelete, onAttend, onUnattend }) => {
  // Récupérer l'ID utilisateur à partir du token
  const token = getToken();
  const decodedToken = jwtDecode(token);
  const userId = decodedToken.id;

  const isAttendee = event.attendees.some(attendee => attendee._id === userId);

  // Exemple d'utilisation de useEffect pour observer event
  useEffect(() => {
    console.log('Event updated:', event);
    // Ici, vous pouvez effectuer d'autres actions en réponse à l'événement
  }, [event]);

  const handleToggleAttend = () => {
    if (isAttendee) {
      onUnattend(event._id);
    } else {
      onAttend(event._id);
    }
  };

  return (
    <div className="event">
      <h3>{event.title}</h3>
      <p>{event.description}</p>
      <p>{new Date(event.date).toLocaleString()}</p>
      <p>Created by: {event.createdBy.name}</p>
      <p>Attendees: {event.attendees.length} / {event.maxAttendees}</p>
      <button className={isAttendee ? "unattend" : "attend"} onClick={handleToggleAttend}>
        {isAttendee ? 'Unattend' : 'Attend'}
      </button>
      {event.createdBy._id === userId && (
        <button className="delete" onClick={() => onDelete(event._id)}>Delete</button>
      )}
    </div>
  );
};

export default Event;
