import React, { useState, useEffect } from 'react';
import { fetchEvents, deleteEvent, attendEvent, unattendEvent, createEvent } from '../api';
import Event from './Event';
import EventForm from './EventForm'; // Assurez-vous d'importer EventForm depuis le bon chemin
import socketIOClient from 'socket.io-client';

const socket = socketIOClient('http://localhost:3000'); // Vérifiez l'adresse et le port de votre serveur WebSocket

const EventList = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const getEvents = async () => {
      const events = await fetchEvents();
      setEvents(events);
    };

    getEvents();
  }, []);

  const handleDelete = async (id) => {
    await deleteEvent(id);
    setEvents(events.filter(event => event._id !== id));
  };

  const handleAttend = async (id) => {
    const updatedEvent = await attendEvent(id);
    setEvents(events.map(event => (event._id === id ? updatedEvent : event)));
  };

  const handleUnattend = async (id) => {
    const updatedEvent = await unattendEvent(id);
    setEvents(events.map(event => (event._id === id ? updatedEvent : event)));
  };

  const handleEventCreated = async (newEvent) => {
    try {
      const createdEvent = await createEvent(newEvent);
      setEvents([...events, createdEvent]); // Ajouter le nouvel événement à la liste existante
    } catch (error) {
      console.error('Error creating event:', error);
    }
  };

  return (
    <div>
      <EventForm onEventCreated={handleEventCreated} />
      {events.map(event => (
        <Event
          key={event._id}
          event={event}
          onDelete={handleDelete}
          onAttend={handleAttend}
          onUnattend={handleUnattend}
        />
      ))}
    </div>
  );
};

export default EventList;
