import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import Event from './Event';
import EventForm from './EventForm';
import { fetchEvents, deleteEvent, attendEvent, unattendEvent, createEvent } from '../api';

const socket = io('http://localhost:3000'); // Assurez-vous que l'URL et le port sont corrects

const EventList = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const getEvents = async () => {
      const events = await fetchEvents();
      setEvents(events);
    };

    getEvents();

    // Écouter les événements WebSocket pour mise à jour en temps réel
    socket.on('eventCreated', (newEvent) => {
      setEvents(prevEvents => [...prevEvents, newEvent]);
    });

    socket.on('eventUpdated', (updatedEvent) => {
      setEvents(prevEvents => 
        prevEvents.map(event => (event._id === updatedEvent._id ? updatedEvent : event))
      );
    });

    socket.on('eventDeleted', (deletedEventId) => {
      setEvents(prevEvents => prevEvents.filter(event => event._id !== deletedEventId));
    });

    return () => {
      // Nettoyer les écouteurs lors du démontage du composant
      socket.off('eventCreated');
      socket.off('eventUpdated');
      socket.off('eventDeleted');
    };
  }, []);

  const handleDelete = async (id) => {
    await deleteEvent(id);
    socket.emit('deleteEvent', id); // Émettre l'événement de suppression via WebSocket
  };

  const handleAttend = async (id) => {
    const updatedEvent = await attendEvent(id);
    socket.emit('updateEvent', updatedEvent); // Émettre l'événement de mise à jour via WebSocket
  };

  const handleUnattend = async (id) => {
    const updatedEvent = await unattendEvent(id);
    socket.emit('updateEvent', updatedEvent); // Émettre l'événement de mise à jour via WebSocket
  };

  const handleCreateEvent = async (newEventData) => {
    try {
      const createdEvent = await createEvent(newEventData);
      socket.emit('createEvent', createdEvent); // Émettre l'événement de création via WebSocket
    } catch (error) {
      console.error('Error creating event:', error);
    }
  };

  return (
    <div>
      <EventForm onEventCreated={handleCreateEvent} />
      {events.map(event => (
        <Event
          key={event._id}
          event={event}
          onDelete={handleDelete}
          onAttend={handleAttend}
          onUnattend={handleUnattend}
        />
      ))}
    </div>
  );
};

export default EventList;
