import React from 'react';

const Event = ({ event, onDelete, onAttend, onUnattend }) => {
  return (
    <div className="event">
      <h3>{event.title}</h3>
      <p>{event.description}</p>
      <p>{new Date(event.date).toLocaleString()}</p>
      <p>Created by: {event.createdBy.name}</p>
      <p>Attendees: {event.attendees.length} / {event.maxAttendees}</p>
      <button onClick={() => onAttend(event._id)}>Attend</button>
      <button onClick={() => onUnattend(event._id)}>Unattend</button>
      <button onClick={() => onDelete(event._id)}>Delete</button>
    </div>
  );
};

export default Event;
